var recluster = require('./');
var path = require('path')

var numCPUs = 2;

var cluster = recluster(path.join(__dirname, './server.js'), {
workers: numCPUs,
readyWhen: 'listening'
});
process.on('SIGUSR2', cluster.reload);
process.on('SIGTERM', cluster.stop);
cluster.on('ready', function(){});
cluster.run();
